package day4_arithmetic_operators;

public class arithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Addition operators +
		
		System.out.println(55+5);
		
		int chairs=140;
		System.out.println(chairs+2);
		
		int morechairs= chairs +5;
		System.out.println(morechairs);
		
		int tables=130;
		int total= tables+chairs;
		System.out.println(total);
		
		//Subtraction
		
		double balance= 1200.40;
		double transaction =56.44;
		System.out.println(balance-transaction);
		balance = balance -transaction;
		System.out.println(balance);
		
		
		
		int myValue = 50;
		System.out.println("Lines of code "+ myValue);
		
		myValue = myValue-2;
		System.out.println("Lines of code " + myValue);
		
		
		//Multiplication
		System.out.println(22*2);
		
		int classes= 5;
		
		int totalLinesOfCodes=myValue*classes;
		
		System.out.println("Total lines of code "+totalLinesOfCodes);
		
		//Division
		
		System.out.println(10.0/3);
		System.out.println(60/3);
		//System.out.println(11/0); // throw exception
		
		
		
		
		
		
		
		
		
		
	}

}
