package day7_control_flow_statements;

public class Task27 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int hour =16;
		
		if (hour<12) {
			System.out.println("good morning");
		
		}
		
		if (hour>=12&&hour<15) {
			System.out.println("good afternoon");
		
		}
		
		if (hour>= 15) {
			System.out.println("good evening");
		}
	}

}
